INSERT INTO `tb_approval` VALUES ('13', '9', '7', '56341234', '1', '<p>（一）《非公司企业法人登记（备案）申请书》。（原件1份）<br>（二）《指定代表或者共同委托代理人授权委托书》（原件1份）及指定代表或委托代理人的身份证件复印件。<br>（三）增设/撤销法人分支机构的提交企业法人的主管部门（出资人）的批准文件。（原件1份）<br>（四）撤销分支机构的，提交被撤销分支机构的营业执照复印件。（复印件1份）<br>（五）企业法人营业执照正副本。（原件1份）<br><br></p>', '0', '0,1', '1,55,54,50,48,44,37,35,34,33,6,5,4,3,2,56', '31', '1', '2017-07-27 13:58:48', '1', '1', '2017-07-27 13:58:48', '9', '非公司企业法人增设、撤销分支机构登记', '0000000000', '30');
