INSERT INTO `tb_business_main` VALUES ('55', '四川智美高科科技公司', 'A100001', '智美', '四川省武侯区广福桥', 'tt2002105368', '中国银行', '602721140108091001', '成立于2009年，长期致力于为各级地方政府提供“互联网+政务”的智慧信息化服务平台顶层咨询规划、建设及整体运营服务，通过对“互联网”、“大数据”、“云计算”、“人工智能”等先进技术手段的创新应用与整合，构建起全新的智慧政务服务体系，打通政、企、民之间的沟通、办理阻塞，促进服务型政府职能转变，大大的提升政务办事效能，提升了社会企业及群众的满意度。', 'b31ecbf0-fa69-4133-be76-a96c7ab8a76f', '10', '0', '101', '80272115', '2017-07-01 05:01:57', '1', '1', '2017-08-10 11:24:03', '15122222233', null);
INSERT INTO `tb_business_main` VALUES ('57', '测试商家', 'tester00', '小东', '碧云路1号', 'gsdjh001', '建行', '62100000', '<p style=\"margin-bottom: 29px; font-size: 16px; line-height: 28px; font-family: &quot;Microsoft Yahei&quot;, Helvetica, sans-serif; text-indent: 2em; background-color: rgb(255, 255, 255);\">前一秒还闷热难耐，后一秒就暴雨如注。夏天的天气，就是这么让人捉摸不定。7月3日下午，在“焖锅”里蒸煮多日的成都人，终于盼来了雨水。这雨，下得突然，下得畅快，也走得匆忙，西边的小伙伴还没感受到雨水的清凉，东边又雨过天晴了。没淋到雨的别着急，昨日的雨，只是“彩排”，雨水主力军还在路上。5日凌晨至7日，四川还有一次强降雨天气过程。</p><p style=\"margin-bottom: 29px; font-size: 16px; line-height: 28px; font-family: &quot;Microsoft Yahei&quot;, Helvetica, sans-serif; text-indent: 2em; background-color: rgb(255, 255, 255);\"><strong>暴雨突袭成都 雨中上演逗趣暖心画面</strong></p><p style=\"margin-bottom: 29px; font-size: 16px; line-height: 28px; font-family: &quot;Microsoft Yahei&quot;, Helvetica, sans-serif; text-indent: 2em; background-color: rgb(255, 255, 255);\">3日午后，阵雨自东向西在成都铺开。金堂(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/JT268167755#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)率先感受到暴雨的“疯狂”，雨水夹杂着大风，吹倒路边树木。下午3点半左右，雨水到达市中心，春熙路暴雨倾泻而下，连伞都没法打。在金牛区解放路一段，一直径约20厘米的大树被连根拔起，所幸无人员受伤。</p><p style=\"margin-bottom: 29px; font-size: 16px; line-height: 28px; font-family: &quot;Microsoft Yahei&quot;, Helvetica, sans-serif; text-indent: 2em; background-color: rgb(255, 255, 255);\">暴雨中的成都，上演着千奇百怪的躲雨场景。在双流(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/zgscsl#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)区街头，小朋友被奶奶放进了买菜的小推车里，只露出两只眼睛，懵懵地看着外面，网友笑称，这是“刚从超市里买的新鲜娃娃”。逗趣的同时，网友也注意到，一旁的奶奶被淋得湿透了。</p><p style=\"margin-bottom: 29px; font-size: 16px; line-height: 28px; font-family: &quot;Microsoft Yahei&quot;, Helvetica, sans-serif; text-indent: 2em; background-color: rgb(255, 255, 255);\">网友“达达旷”在地铁天府广场站拍下了一张暖心照片：地铁口，暴雨如注，一个穿着红色裤子的男子，手里举着伞，伞下，是一位残疾的老人。据该网友描述，当时，突然下起暴雨，所有人都跑到地铁站躲雨，途中的老人腿脚不方便，一位打着伞的小哥冲了上去，为他撑起了伞。</p><p style=\"margin-bottom: 29px; font-size: 16px; line-height: 28px; font-family: &quot;Microsoft Yahei&quot;, Helvetica, sans-serif; text-indent: 2em; background-color: rgb(255, 255, 255);\"><strong>主降雨还没来 明起12市将迎强降雨</strong></p><p style=\"margin-bottom: 29px; font-size: 16px; line-height: 28px; font-family: &quot;Microsoft Yahei&quot;, Helvetica, sans-serif; text-indent: 2em; background-color: rgb(255, 255, 255);\">当大多数成都人都在感受酣畅淋漓的暴雨时，西边的小伙伴依旧在“焖锅”中求雨。还未收到“雨水包裹”不要急，雨水主力军还在路上呢。</p><p style=\"margin-bottom: 29px; font-size: 16px; line-height: 28px; font-family: &quot;Microsoft Yahei&quot;, Helvetica, sans-serif; text-indent: 2em; background-color: rgb(255, 255, 255);\">据四川省气象局(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/scqx028#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)最新气象资料分析，预计5日凌晨至7日，我省有一次强降雨天气过程。其中盆地西部、北部有大雨到暴雨，局部大暴雨（110～140毫米），强降雨落区在广元(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/vguangyuan#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)、绵阳&nbsp;(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/myxcb03#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)、德阳(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/scwbdy2012#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)、成都、雅安(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/v-copeyaan#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)、乐山(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/vleshan#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)、眉山(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/msxwbwb#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)、资阳(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/vziyang#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)、遂宁(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/cnsnfb#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)、南充(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/nanchongcity#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)、巴中、达州(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/weidazhou2011#pref=qqcom.keyword\" target=\"_blank\" style=\"outline: none; color: rgb(0, 0, 0); border-bottom: 1px dotted rgb(83, 109, 166);\">微博</a>)12市；川西高原有中到大雨，局部暴雨，攀西地区有大雨，局部暴雨。</p><p style=\"margin-bottom: 29px; font-size: 16px; line-height: 28px; font-family: &quot;Microsoft Yahei&quot;, Helvetica, sans-serif; text-indent: 2em; background-color: rgb(255, 255, 255);\">今天白天，成都依旧是阵雨穿插播放，到了晚上，雨势将加强。成都的雨水主要集中在5日晚上到6日白天，雨量大雨，西部和南部雨量可达大雨到暴雨。</p>', '1,2,3', '10', '0', null, 'cssj', '2017-07-04 03:21:48', '1', '1', '2017-07-04 03:21:48', '15828548205', null);
