INSERT INTO `tb_business_main` VALUES ('59', '武汉湖锦酒楼管理有限公司', 'A100001', '郭玉芳', '武汉市武昌区八一路105号', '4201002104522', '中国银行', '4201002104500', '<p>湖锦酒楼全称武汉湖锦酒楼管理有限公司，湖锦酒楼位列全国餐饮企业百强，成立与上世纪九十年代初，享有“鄂菜十大名店”和“中华餐饮名店”荣誉称号，越各派菜系精华，经典传承与时尚创新兼备，为武汉市品尝湖北特色、商务宴请之首选。</p><p>武汉湖锦酒店管理有限公司地处武汉市武昌区八一路105号，注册资金为100万元，联系人是毛必祥。公司于1999年在武汉市工商行政管理局武昌分局登记注册，注册号为4201002104522，企业类型是有限责任公司(自然人投资或控股)。目前的经营范围是中餐供应。</p>', '5f1e2e04-cb94-4297-80b7-7e27b4b358cd', '10', '0', null, 'whjh00001', '2017-07-05 01:37:57', '1', '1', '2017-07-27 14:25:02', '13500000000', null);
