INSERT INTO `tb_session_message` VALUES ('2048', '82', '', '1', '1', '1', '1', '', '1501579745044', '1', '2', '工伤保险康复待遇申请的办理流程是：\n第一步：单位经办人持申请材料至参保地社保中心综合服务窗口申请工伤保险康复待遇，经核实符合法定条件、材料齐全的予以受理；\n第二步：单位经办人持已审核的《成都市工伤（职业病）康复治疗申请表》至成都市医保局工伤处审批；\n第三步：审批确认后再到成都市工伤职工定点康复中心进行康复治疗。\r\n您可能还关注以下的问题：\r\n1. 变更性别的办理材料\r\n2. 工伤保险康复待遇申请的办理条件\r\n3. 办理工伤认定需要多久\r\n4. 城镇职工医疗保险转入多久能办好\r\n5. 联络员备案的办理材料\r\n', 'user', 'xiaoi', '', '1501579745044_82', '1', '2017-08-01 17:29:01', '82', null, null);
