-- ----------------------------
-- Procedure structure for `zhou_change_status_procedure_activity`
-- ----------------------------
DROP PROCEDURE IF EXISTS `zhou_change_status_procedure_activity`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `zhou_change_status_procedure_activity`()
BEGIN
-- 活动有效期过了后,设置为已过期.状态为 10
-- 定义接收游标数据的变量, 满足过期条件的活动的id 

  DECLARE cur_activity_id int;
    -- 遍历数据结束标志
  DECLARE done INT DEFAULT FALSE;
  -- 游标
  DECLARE cur CURSOR FOR SELECT activity_id 
                         FROM joygov_test.tb_activity 
                         where status > 10
                         and   str_to_date(end_time, '%Y-%m-%d %T') < now();

  -- 将结束标志绑定到游标
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
  -- 打开游标
  OPEN cur;
  
  -- 开始循环
  read_loop: LOOP
    -- 提取游标里的数据，这里只有一个，多个的话也一样；
    FETCH cur INTO cur_activity_id;
    -- 声明结束的时候
    IF done THEN
      LEAVE read_loop;
    END IF;
    -- 这里做你想做的循环的事件
    update joygov_test.tb_activity 
    set status = 10 
    where activity_id = cur_activity_id;

  END LOOP;
  -- 关闭游标
  CLOSE cur;
END
;;
DELIMITER ;

