-- ----------------------------
-- Function structure for `intbetween`
-- ----------------------------
DROP FUNCTION IF EXISTS `intbetween`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` FUNCTION `intbetween`(rowInt int(1),beginInt varchar(19),endInt varchar(19)) RETURNS tinyint(1)
BEGIN  
-- 数字区间比较函数，减少在xml的配置
-- 数据中数字，开始数字，结束数字 ,返回tinyint(1)，1为 true，0为 false
    -- 如果 开始数字，结束数字都为空，表示不需要比较,返回成功
    if(isnull(beginInt) and isnull(endInt)) then
		return 1;
    end if;
    -- 其中之一不为空，需要比较了，被比较的数字为空，不符合条件返回 false
    if(isnull(rowInt)) then
        return 0;
	end if;
    
    -- 没有开始数字,只比较结束数字
    if(isnull(beginInt)) then
		return rowInt <= endInt;
    end if;
    -- 没有结束数字,只比开始数字
    if(isnull(endInt)) then
		return rowInt >= beginInt;
    end if;
    -- 开始数字，结束数字都要比较
    return rowInt >= beginInt  and  rowInt <= endInt;
END
;;
DELIMITER ;

-- ----------------------------
-- Function structure for `timebetween`
-- ----------------------------
DROP FUNCTION IF EXISTS `timebetween`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` FUNCTION `timebetween`(rowDate varchar(19),beginDate varchar(19),endDate varchar(19)) RETURNS tinyint(1)
BEGIN  
-- 时间区间比较函数，减少在xml的配置
-- 数据中时间，开始时间，结束时间 ,返回tinyint(1)，1为 true，0为 false

    -- 如果 开始时间，结束时间都为空，表示不需要比较,返回成功
    if(isnull(beginDate) and isnull(endDate)) then
		return 1;
    end if;
    -- 其中之一不为空，表示需要比较了，被比较的日期为空，不符合条件返回 false
    if(isnull(rowDate)) then
        return 0;
	end if;
    -- 假设时间都是这样正确的,时间转换函数 str_to_date('2016-01-02', '%Y-%m-%d %T')
    -- 没有开始时间,只比较结束时间
    if(isnull(beginDate)) then
		return str_to_date(rowDate,'%Y-%m-%d %T') <= date_add(str_to_date(endDate,'%Y-%m-%d %T'), interval 1 day) ;
    end if;
    -- 没有结束时间,只比开始时间
    if(isnull(endDate)) then
		return str_to_date(rowDate,'%Y-%m-%d %T') >= str_to_date(beginDate,'%Y-%m-%d %T');
    end if;
    -- 开始时间，结束时间都要比较
    return str_to_date(rowDate,'%Y-%m-%d %T') >= str_to_date(beginDate,'%Y-%m-%d %T')  and str_to_date(rowDate,'%Y-%m-%d %T') <= date_add(str_to_date(endDate,'%Y-%m-%d %T'), interval 1 day) ;
END
;;
DELIMITER ;

